var container = $('deploy-content');
var BASE_API_URL = 'https://api.github.com/repos/phenotips/';
var BASE_GITREPO_URL = 'https://github.com/phenotips/';
var SERVICE_URL = new XWiki.Document('PCTestDeploymentService', 'PhenomeCentral').getURL('get');

var repos = [
            'phenotips',
            'patient-network',
            'remote-matching',
            'phenomecentral.org'
            ];

var selects = {};

var createCORSRequest = function(method, url) {
    var xhr = new XMLHttpRequest();
    if ("withCredentials" in xhr) {
       // XHR for Chrome/Firefox/Opera/Safari.
      xhr.open(method, url, true);
    } else if (typeof XDomainRequest != "undefined") {
      // XDomainRequest for IE.
      xhr = new XDomainRequest();
      xhr.open(method, url);
    } else {
      // CORS not supported.
      xhr = null;
    }
    return xhr;
};


var generateBranchSelect = function (repo, jsonArray) {
    var repoLink = new Element('a', {'href': BASE_GITREPO_URL + repo, 'target': '_blank'}).update(repo);
    var selectDiv = new Element('div', {'class' : 'repo-select'}).insert(new Element('label', {'class' : 'repo-label'}).update(repoLink));
    var select = new Element('select', {'id': 'repo-select-' + repo});
    jsonArray.each(function(branch) {
        var branchData = { 'value': branch.name };
        if (branch.name == "master") {
            branchData["selected"] = true;
        }
        select.insert(new Element('option', branchData).update(branch.name));
    });
    selectDiv.insert(select);
    return selectDiv;
}

var requestReposInfo = function () {
  var requested = 0;
  repos.each(function (repo) {
    var xhr = createCORSRequest('GET', BASE_API_URL + repo + '/branches?per_page=100');
    if (!xhr) { return; } // CORS not supported
    // Response handlers
    xhr.onload = function() {
        var response = JSON.parse(xhr.responseText);
        if (response && Array.isArray(response)) {
            selects[repo] = generateBranchSelect(repo, response);
            requested++;
            if (requested == repos.length) {
                updateUI();
            }
        } else {
            console.log("[!] Error parsing patient data JSON");
        }
    };

    xhr.onerror = function() {
        return;
    };

    xhr.send();
  });
}

var attachServerDeleteListeners = function () {
    $$('.delete-server').invoke('observe', 'click', function(clickEvent) {
        clickEvent.stop();
        var item = clickEvent.element();
        var serverName = item.up('tr').down('.server-name').innerHTML;
        item.blur();
        if (item.disabled) {
          // Do nothing if the button was already clicked and it's waiting for a response from the server.
          return;
        } else {
            new XWiki.widgets.ConfirmedAjaxRequest(SERVICE_URL, {
                  parameters: {
                    'action' : 'delete',
                    'buildName' : serverName
                  },
                  method: "get",
                  onCreate : function() {
                      console.log('Sending');
                  },
                  onSuccess: function(response) {
                      console.log('Success');
                      item.up('tr').hide();
                  },
                  onComplete : function() {
                      console.log('Complete');
                  },
                  onFailure : function() {
                      console.log('Failed');
                  }
                },
                /* Interaction parameters */
                {
                    confirmationText: 'Are you sure that you want to delete instance <span class="code">' + serverName + '</span>?'
                }
            );
        }
    });
}

var attachLoadDataListeners = function () {
    $$('.button.load-data').invoke('observe', 'click', function(clickEvent) {
        clickEvent.stop();
        var item = clickEvent.element();
        var serverIP = item.up('tr').down('.ip a').innerHTML;
        var dataName = item.up('td').down('select').value;
        item.blur();
        if (item.disabled) {
            // Do nothing if the button was already clicked and it's waiting for a response from the server.
            return;
        } else {
            new Ajax.Request(SERVICE_URL, {
                parameters: {
                    'action'   : 'load-data',
                    'ip'       : serverIP,
                    'dataName' : dataName
                },
                method: "get",
                onCreate : function() {
                    console.log('Sending');
                },
                onSuccess: function(response) {
                    console.log('Success');
                },
                onComplete : function() {
                    console.log('Complete');
                },
                onFailure : function() {
                    console.log('Failed');
                }
            });
        }
    });
}


var selectProject = function(name) {
    if (name == "PhenoTips") {
        $('repo-select-phenotips').up().show();
        $('repo-select-patient-network').up().hide();
        $('repo-select-remote-matching').up().hide();
        $('repo-select-phenomecentral.org').up().hide();
    } else {
        $('repo-select-phenotips').up().hide();
        $('repo-select-patient-network').up().show();
        $('repo-select-remote-matching').up().show();
        $('repo-select-phenomecentral.org').up().show();
    }
}

var attachProjectSelectionListeners = function () {
    $$('input[name="project"]').invoke('observe', 'click', function(event) {
        var item = event.element();
        selectProject(item.value);
    });
}

var attachButtonHandlers  = function () {
    attachServerDeleteListeners();
    attachLoadDataListeners();
    attachProjectSelectionListeners();
}

var updateUI = function () {
    repos.each(function (repo) {
        container.insert(selects[repo]);
    });

    var deployNameInput = new Element('input', {'class' : 'deploy-input', 'type' : 'text', 'id' : 'deploy-name'});
    var deployNameDiv = new Element('div', {'class' : 'deploy-name'});
    deployNameDiv.insert(new Element('label', {'class' : 'deploy-name-label'}).update('Deploy build name:'));
    deployNameDiv.insert(deployNameInput);
    container.insert(deployNameDiv);

    // var button = new Element('input', {'class' : 'button', 'type' : 'button', 'id':'deploy', 'value':'Deploy'});
    var button = new Element('span', {'class' : 'buttonwrapper'});
    var buttonA = new Element('a', {'class' : 'button deploy-button', 'id':'deploy', 'href' : '#'})
                                   .insert(new Element('span', {'class' : 'fa fa-upload'}).update(" "))
                                   .insert(new Element('span').update("Deploy"));
    container.insert(button.insert(buttonA));

    var deploying = false;

    var onDeploy = function (event) {
        if (deploying) {
            return;
        }
        new Ajax.Request(SERVICE_URL, {
            parameters: {
                'pnBranchName' : $('repo-select-patient-network').value,
                'rmBranchName' : $('repo-select-remote-matching').value,
                'pcBranchName' : $('repo-select-phenomecentral.org').value,
                'ptBranchName' : $('repo-select-phenotips').value,
                'buildName'    : $('deploy-name').value,
                'project'      : $$('input[name="project"]:checked')[0].value
            },
            method: "get",
            onCreate : function() {
                deploying = true;
                console.log('Sending');
                $('deploy').addClassName("disabled-button");
                behavior = {};
                var dialogParameters = {
                      confirmationText: "A request to start a new VM will be sent - see deployment log for progress details",
                      yesButtonText: "OK",
                      noButtonText: ""
                };
                var actionPerformedDialog = new XWiki.widgets.ConfirmationBox(behavior, dialogParameters);
                //setTimeout(function() { window.open("../resources/latest_deploy.log");}, 500);
            },
            onSuccess: function(response) {
                console.log('Success');
                behavior = {
                    onYes: function() {
                        $('xwikimaincontainerinner').hide();
                        location.reload();
                    }
                };
                var dialogParameters = {
                      confirmationText: "A new test VM has been deployed. The page will be refreshed now to reflect the changed. ",
                      yesButtonText: "OK",
                      noButtonText: ""
                };
                var actionPerformedDialog = new XWiki.widgets.ConfirmationBox(behavior, dialogParameters);
            },
            onComplete : function() {
                console.log('Complete');
                $('deploy').removeClassName("disabled-button");
                deploying = false;
            }
        });
    };

    button.observe('click', onDeploy);

    selectProject("PhenomeCentral");
}

document.observe('xwiki:dom:loaded', function() {
    requestReposInfo();
    attachButtonHandlers();
});

